package JavaBasics;

public class A {
	
	public A() {
		System.out.println("Inside Parent class Constructor");
	}
	public A(int x, int y) {
		System.out.println("Inside 2 parametrized Parent class Constructor. Value of x and y are: " + x + " & " + y);
	}
	public A(int i)
	{
		System.out.println("Inside parameterized constructor of Class A. Valuye of i is " + i);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		}
	

}
